    cd excelence/frontend && npm install

# Run backend tests
test-backend:
    @echo "Running backend tests..."
    cd excelence/backend && pytest

# Run frontend tests
test-frontend:
    @echo "Running frontend tests..."
    cd excelence/frontend && npm test

# Run all tests
test:
    @just test-backend
    @just test-frontend

# Lint backend code
lint-backend:
    @echo "Linting backend code..."
    cd excelence/backend && ruff check .

# Lint frontend code
lint-frontend:
    @echo "Linting frontend code..."
    cd excelence/frontend && npm run lint

# Lint all code
lint:
    @just lint-backend
    @just lint-frontend

# Format backend code
format-backend:
    @echo "Formatting backend code..."
    cd excelence/backend && ruff format .

# Format frontend code
format-frontend:
    @echo "Formatting frontend code..."
    cd excelence/frontend && npm run format

# Format all code
format:
    @just format-backend
    @just format-frontend

# Clean build artifacts
clean:
    @echo "Cleaning build artifacts..."
    find . -type d -name "__pycache__" -exec rm -rf {} + 2>/dev/null || true
    find . -type d -name "node_modules" -exec rm -rf {} + 2>/dev/null || true
    find . -type d -name ".pytest_cache" -exec rm -rf {} + 2>/dev/null || true
    find . -type d -name "dist" -exec rm -rf {} + 2>/dev/null || true

