import { writable } from 'svelte/store';
import { browser } from '$app/environment';
import api from '$lib/services/api';

const createAuthStore = () => {
  const { subscribe, set } = writable({
    isAuthenticated: browser && !!localStorage.getItem('jwt_token'),
    token: browser && localStorage.getItem('jwt_token'),
  });

  return {
    subscribe,
    login: async (email, password) => {
      const body = new URLSearchParams();
      body.append('username', email);
      body.append('password', password);

      const response = await api.post('/api/v1/auth/login', body, {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
      });
      if (response.access_token) {
        if (browser) {
          localStorage.setItem('jwt_token', response.access_token);
        }
        set({ isAuthenticated: true, token: response.access_token });
      }
    },
    logout: () => {
      if (browser) {
        localStorage.removeItem('jwt_token');
      }
      set({ isAuthenticated: false, token: null });
    },
  };
};

export const authStore = createAuthStore();

const BASE_URL = import.meta.env.VITE_API_BASE_URL || 'http://localhost:8000';

const getHeaders = () => {
  const headers = {
    'Content-Type': 'application/json',
  };
  const token = localStorage.getItem('jwt_token');
  if (token) {
    headers['Authorization'] = `Bearer ${token}`;
  }
  return headers;
};

const handleResponse = async (response) => {
  if (!response.ok) {
    const error = await response.json();
    throw new Error(error.detail || 'API request failed');
  }
  return response.json();
};

const api = {
  get: async (endpoint) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
      method: 'GET',
      headers: getHeaders(),
    });
    return handleResponse(response);
  },
  post: async (endpoint, data, options = {}) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
      method: 'POST',
      headers: { ...getHeaders(), ...options.headers },
      body: data instanceof URLSearchParams ? data.toString() : JSON.stringify(data),
    });
    return handleResponse(response);
  },
};

export default api;

