import { describe, it, expect, vi } from 'vitest';
import { render, fireEvent } from '@testing-library/svelte';
import TransactionForm from './TransactionForm.svelte';

const mockCategories = [
	{ id: 1, name: 'Groceries' },
	{ id: 2, name: 'Salary' }
];

describe('TransactionForm', () => {
	it('renders and dispatches a save event with the correct data', async () => {
		const { getByLabelText, getByText, component } = render(TransactionForm, {
			props: { categories: mockCategories }
		});

		const amountInput = getByLabelText('Amount');
		const typeSelect = getByLabelText('Type');
		const dateInput = getByLabelText('Date');
		const categorySelect = getByLabelText('Category');
		const descriptionInput = getByLabelText('Description');
		const saveButton = getByText('Save');

		// Fill out the form
		await fireEvent.input(amountInput, { target: { value: '50' } });
		await fireEvent.change(typeSelect, { target: { value: 'expense' } });
		await fireEvent.input(dateInput, { target: { value: '2025-12-24' } });
		await fireEvent.change(categorySelect, { target: { value: '1' } });
