# Justfile for Excelence project

# Default recipe - show available commands
default:
    @just --list

# Start both frontend and backend in parallel
dev:
    @echo "Starting frontend and backend..."
    @just dev-backend & just dev-frontend

# Start backend server
dev-backend:
    @echo "Starting backend server on port 8000..."
    cd excelence/backend && uv run uvicorn main:app --reload --host 0.0.0.0 --port 8000

# Start frontend dev server
dev-frontend:
    @echo "Starting frontend dev server on port 5173..."
    cd excelence/frontend && npm run dev

# Install all dependencies
install:
    @echo "Installing dependencies..."
    @just install-backend
    @just install-frontend

# Install backend dependencies
install-backend:
    @echo "Installing backend dependencies..."
    cd excelence/backend && uv pip install -e ".[dev]"

# Install frontend dependencies
install-frontend:
    @echo "Installing frontend dependencies..."
    cd excelence/frontend && npm install

# Run backend tests
test-backend:
    @echo "Running backend tests..."
    cd excelence/backend && uv run pytest

# Run frontend tests
test-frontend:
    @echo "Running frontend tests..."
    cd excelence/frontend && npm test

# Run all tests
test:
    @just test-backend
    @just test-frontend

# Lint backend code
lint-backend:
    @echo "Linting backend code..."
    cd excelence/backend && uv run ruff check .

# Lint frontend code
lint-frontend:
    @echo "Linting frontend code..."
    cd excelence/frontend && npm run lint

# Lint all code
lint:
    @just lint-backend
    @just lint-frontend

# Format backend code
format-backend:
    @echo "Formatting backend code..."
    cd excelence/backend && uv run ruff format .

# Format frontend code
format-frontend:
    @echo "Formatting frontend code..."
    cd excelence/frontend && npm run format

# Format all code
format:
    @just format-backend
    @just format-frontend

# Clean build artifacts
clean:
    @echo "Cleaning build artifacts..."
    find . -type d -name "__pycache__" -exec rm -rf {} + 2>/dev/null || true
    find . -type d -name "node_modules" -exec rm -rf {} + 2>/dev/null || true
    find . -type d -name ".pytest_cache" -exec rm -rf {} + 2>/dev/null || true
    find . -type d -name "dist" -exec rm -rf {} + 2>/dev/null || true

const BASE_URL = import.meta.env.VITE_API_BASE_URL || 'http://localhost:8000';

const getHeaders = () => {
  const headers = {
    'Content-Type': 'application/json',
  };
  // @ts-ignore
  const token = localStorage.getItem('jwt_token');
  if (token) {
    // @ts-ignore
    headers['Authorization'] = `Bearer ${token}`;
  }
  return headers;
};

const handleResponse = async (response) => {
  if (response.status === 204) {
    return {}; // No content to parse
  }
  if (!response.ok) {
    const error = await response.json();
    throw new Error(error.detail || 'API request failed');
  }
  return response.json();
};

const api = {
  get: async (endpoint) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
      method: 'GET',
      headers: getHeaders(),
    });
    return handleResponse(response);
  },
  post: async (endpoint, data, options = {}) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
      method: 'POST',
      headers: { ...getHeaders(), ...options.headers },
      body: data instanceof URLSearchParams ? data.toString() : JSON.stringify(data),
    });
    return handleResponse(response);
  },
  put: async (endpoint, data) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
        method: 'PUT',
        headers: getHeaders(),
        body: JSON.stringify(data)
    });
    return handleResponse(response);
  },
  delete: async (endpoint) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
        method: 'DELETE',
        headers: getHeaders()
    });
    return handleResponse(response);
  }
};

// --- Category Specific Functions ---
export const getCategories = () => {
    return api.get('/api/v1/categories/');
};

export const createCategory = (name: string) => {
    return api.post('/api/v1/categories/', { name });
};

export const updateCategory = (id: number, name: string) => {
    return api.put(`/api/v1/categories/${id}`, { name });
};

export const deleteCategory = (id: number) => {
    return api.delete(`/api/v1/categories/${id}`);
};

// --- Transaction Specific Functions ---
export const getTransactions = () => {
    return api.get('/api/v1/transactions/');
};

export const createTransaction = (transaction) => {
    return api.post('/api/v1/transactions/', transaction);
};

export const updateTransaction = (id, transaction) => {
    return api.put(`/api/v1/transactions/${id}`, transaction);
};


export default api;

import { describe, it, expect, vi } from 'vitest';
import { render, fireEvent, screen } from '@testing-library/svelte';
import userEvent from '@testing-library/user-event';
import TransactionForm from './TransactionForm.svelte';

describe('TransactionForm', () => {
    const mockCategories = [
        { id: 'a4f2b4b0-7b6d-4b7b-8b0a-0e9f3b5a4b1e', name: 'Groceries' },
        { id: 'c2c3e1e0-7b6d-4b7b-8b0a-0e9f3b5a4b1e', name: 'Salary' }
    ];

    const mockTransaction = {
        id: 't1',
        amount: 50.00,
        type: 'expense',
        date: '2025-12-01',
        description: 'Initial purchase',
        category_id: mockCategories[0].id
    };

	it('calls the onSave handler with form data on submit', async () => {
		const user = userEvent.setup();
		const onSave = vi.fn();
		render(TransactionForm, {
			props: {
				categories: mockCategories,
				onSave,
				onClose: vi.fn()
			}
		});

        // Fill out the form
        await user.type(screen.getByLabelText('Amount'), '123.45');
        await user.selectOptions(screen.getByLabelText('Type'), 'expense');
        await fireEvent.input(screen.getByLabelText('Date'), { target: { value: '2025-12-06' } });
        await user.selectOptions(screen.getByLabelText('Category'), mockCategories[0].id);
        await user.type(screen.getByLabelText('Description'), 'Test purchase');

        // Submit the form
        await fireEvent.submit(screen.getByTestId('transaction-form'));

        expect(onSave).toHaveBeenCalledWith({
            amount: 123.45,
            type: 'expense',
            date: '2025-12-06',
            description: 'Test purchase',
            category_id: mockCategories[0].id
        });
    });

	it('calls the onClose handler when cancel is clicked', async () => {
		const user = userEvent.setup();
		const onClose = vi.fn();
		render(TransactionForm, {
			props: {
				categories: mockCategories,
				onSave: vi.fn(),
				onClose
			}
		});

        await user.click(screen.getByText('Cancel'));
        expect(onClose).toHaveBeenCalled();
    });

    it('pre-fills the form when a transaction prop is provided', () => {
        render(TransactionForm, {
            props: {
				categories: mockCategories,
				transaction: mockTransaction,
				onSave: vi.fn(),
				onClose: vi.fn()
			}
        });

        expect(screen.getByLabelText('Amount').value).toBe('50');
        expect(screen.getByLabelText('Type').value).toBe('expense');
        expect(screen.getByLabelText('Date').value).toBe('2025-12-01');
        expect(screen.getByLabelText('Category').value).toBe(mockTransaction.category_id);
        expect(screen.getByLabelText('Description').value).toBe('Initial purchase');
        expect(screen.getByText('Edit Transaction')).toBeInTheDocument();
    });
});

