from typing import List, Optional
from fastapi import APIRouter, HTTPException, Depends
from pydantic import BaseModel, Field
from app.db.session import supabase
from app.api import deps
from app.crud import transactions as crud_transactions
import uuid
from datetime import date

router = APIRouter()

# --- Pydantic Models ---
class TransactionCreate(BaseModel):
    amount: float
    type: str  # "income" or "expense"
    date: date
    description: Optional[str] = None
    category_id: uuid.UUID

class Transaction(BaseModel):
    id: uuid.UUID
    amount: float
    type: str
    date: date
    description: Optional[str] = None
    user_id: uuid.UUID
    category_id: uuid.UUID

class TransactionUpdate(BaseModel):
    amount: Optional[float] = None
    type: Optional[str] = None  # "income" or "expense"
    date: Optional[date] = None
    description: Optional[str] = None
    category_id: Optional[uuid.UUID] = None

# --- API Endpoints ---
@router.get("/", response_model=List[Transaction])
def list_transactions(user: dict = Depends(deps.get_current_user)):
    """
    Retrieve all transactions for the current user.
    """
    try:
        user_id = user.user.id
        response = supabase.table('transactions').select('*').match({
            'user_id': user_id
        }).order('date', desc=True).execute()

        if not response.data:
            return []

        return [Transaction(**t) for t in response.data]
    except Exception as e:
        raise HTTPException(status_code=400, detail=str(e))

@router.put("/{transaction_id}", response_model=Transaction)
def update_transaction(transaction_id: uuid.UUID, transaction: TransactionUpdate, user: dict = Depends(deps.get_current_user)):
    """
    Update a transaction for the current user.
    """
    try:
        user_id = user.user.id
        update_data = transaction.dict(exclude_unset=True)

        if not update_data:
            raise HTTPException(status_code=400, detail="No update data provided.")

        response = supabase.table('transactions').update(update_data).match({
            'id': transaction_id,
            'user_id': user_id
        }).execute()

        if not response.data:
            raise HTTPException(status_code=404, detail="Transaction not found or user does not have permission.")

        updated_transaction = response.data[0]
        return Transaction(**updated_transaction)
    except Exception as e:
        raise HTTPException(status_code=400, detail=str(e))

@router.post("/", response_model=Transaction)
def create_transaction(transaction: TransactionCreate, user: dict = Depends(deps.get_current_user)):
    """
    Create a new transaction for the current user.
    """
    try:
        user_id = user.user.id
        response = supabase.table('transactions').insert({
            "amount": transaction.amount,
            "type": transaction.type,
            "date": str(transaction.date),
            "description": transaction.description,
            "category_id": transaction.category_id,
            "user_id": user_id
        }).execute()

        if not response.data:
            raise HTTPException(status_code=500, detail="Failed to create transaction.")

        created_transaction = response.data[0]
        return Transaction(**created_transaction)
    except Exception as e:
        raise HTTPException(status_code=400, detail=str(e))

@router.delete("/{transaction_id}", response_model=dict)
def delete_transaction(transaction_id: uuid.UUID, user: dict = Depends(deps.get_current_user)):
    """
    Delete a transaction for the current user.
    """
    try:
        user_id = user.user.id
        success = crud_transactions.delete_transaction(transaction_id, user_id)

        if not success:
            raise HTTPException(status_code=404, detail="Transaction not found or user does not have permission.")

        return {"detail": "Transaction deleted successfully"}
    except Exception as e:
        if isinstance(e, HTTPException):
            raise e
        raise HTTPException(status_code=400, detail=str(e))

import pytest
from fastapi.testclient import TestClient
from unittest.mock import MagicMock, PropertyMock
import uuid

USER_ID = uuid.uuid4()

class MockSupabaseUser:
    id = USER_ID

class MockUserResponse:
    user = MockSupabaseUser()

@pytest.fixture
def mock_supabase_auth(mocker):
    return mocker.patch(
        "app.api.deps.supabase.auth.get_user",
        return_value=MockUserResponse()
    )

@pytest.fixture
def mock_crud_supabase(mocker):
    # Mocking the supabase client used in the crud module
    return mocker.patch("app.crud.transactions.supabase.table")

def test_delete_transaction_success(client: TestClient, mock_supabase_auth, mock_crud_supabase):
    mock_execute = MagicMock()
    # Return a list with one item to simulate successful deletion
    type(mock_execute).data = PropertyMock(return_value=[{"id": str(uuid.uuid4())}])

    mock_crud_supabase.return_value.delete.return_value.match.return_value.execute.return_value = mock_execute

    transaction_id = uuid.uuid4()
    response = client.delete(
        f"/api/v1/transactions/{transaction_id}",
        headers={"Authorization": "Bearer fake-token"}
    )

    assert response.status_code == 200
    assert response.json() == {"detail": "Transaction deleted successfully"}

    # Verify the correct calls were made
    mock_crud_supabase.assert_called_with('transactions')
    mock_crud_supabase.return_value.delete.assert_called_once()
    mock_crud_supabase.return_value.delete.return_value.match.assert_called_with({
        'id': str(transaction_id),
        'user_id': str(USER_ID)
    })

def test_delete_transaction_not_found(client: TestClient, mock_supabase_auth, mock_crud_supabase):
    mock_execute = MagicMock()
    # Return empty list to simulate not found or not owned by user
    type(mock_execute).data = PropertyMock(return_value=[])

    mock_crud_supabase.return_value.delete.return_value.match.return_value.execute.return_value = mock_execute

    transaction_id = uuid.uuid4()
    response = client.delete(
        f"/api/v1/transactions/{transaction_id}",
        headers={"Authorization": "Bearer fake-token"}
    )

    assert response.status_code == 404
    assert response.json() == {"detail": "Transaction not found or user does not have permission."}

const BASE_URL = import.meta.env.VITE_API_BASE_URL || 'http://localhost:8000';

const getHeaders = () => {
  const headers = {
    'Content-Type': 'application/json',
  };
  // @ts-ignore
  const token = localStorage.getItem('jwt_token');
  if (token) {
    // @ts-ignore
    headers['Authorization'] = `Bearer ${token}`;
  }
  return headers;
};

const handleResponse = async (response) => {
  if (response.status === 204) {
    return {}; // No content to parse
  }
  if (!response.ok) {
    const error = await response.json();
    throw new Error(error.detail || 'API request failed');
  }
  return response.json();
};

const api = {
  get: async (endpoint) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
      method: 'GET',
      headers: getHeaders(),
    });
    return handleResponse(response);
  },
  post: async (endpoint, data, options = {}) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
      method: 'POST',
      headers: { ...getHeaders(), ...options.headers },
      body: data instanceof URLSearchParams ? data.toString() : JSON.stringify(data),
    });
    return handleResponse(response);
  },
  put: async (endpoint, data) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
      method: 'PUT',
      headers: getHeaders(),
      body: JSON.stringify(data)
    });
    return handleResponse(response);
  },
  delete: async (endpoint) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
      method: 'DELETE',
      headers: getHeaders()
    });
    return handleResponse(response);
  }
};

// --- Category Specific Functions ---
export const getCategories = () => {
  return api.get('/api/v1/categories/');
};

export const createCategory = (name: string) => {
  return api.post('/api/v1/categories/', { name });
};

export const updateCategory = (id: number, name: string) => {
  return api.put(`/api/v1/categories/${id}`, { name });
};

export const deleteCategory = (id: number) => {
  return api.delete(`/api/v1/categories/${id}`);
};

// --- Transaction Specific Functions ---
export const getTransactions = () => {
  return api.get('/api/v1/transactions/');
};

export const createTransaction = (transaction) => {
  return api.post('/api/v1/transactions/', transaction);
};

export const updateTransaction = (id, transaction) => {
  return api.put(`/api/v1/transactions/${id}`, transaction);
};

export const deleteTransaction = (id) => {
  return api.delete(`/api/v1/transactions/${id}`);
};

// --- Auth Specific Functions ---
export const registerUser = (email: string, password: string) => {
  return api.post('/api/v1/auth/signup', { email, password });
};

export const loginUser = (email: string, password: string) => {
  const formData = new URLSearchParams();
  formData.append('username', email);
  formData.append('password', password);
  return api.post('/api/v1/auth/login', formData, {
    headers: { 'Content-Type': 'application/x-www-form-urlencoded' }
  });
};

export default api;

import { render, screen, waitFor } from '@testing-library/svelte';
import { describe, it, expect, vi, beforeEach } from 'vitest';
import TransactionList from './TransactionList.svelte';

// Mock the API service
vi.mock('$lib/services/api', () => ({
  getTransactions: vi.fn(),
  getCategories: vi.fn(() => Promise.resolve({ data: [] })),
}));

import { getTransactions } from '$lib/services/api';

const mockTransactions = [
  {
    id: '1',
    date: '2025-12-05',
    description: 'Coffee',
    amount: 5.00,
    type: 'expense',
    category_id: 'cat1',
  },
  {
    id: '2',
    date: '2025-12-04',
    description: 'Salary',
    amount: 2000.00,
    type: 'income',
    category_id: 'cat2',
  },
];


describe('TransactionList Component', () => {
  beforeEach(() => {
    vi.clearAllMocks();
  });

  it('renders a list of transactions on successful fetch', async () => {
    getTransactions.mockResolvedValue({ data: mockTransactions });
    render(TransactionList);

    await waitFor(() => {
      expect(screen.getByText('Coffee')).toBeInTheDocument();
      expect(screen.getByText('Salary')).toBeInTheDocument();
      expect(screen.getByText('-$5.00')).toBeInTheDocument();
      expect(screen.getByText('+$2000.00')).toBeInTheDocument();
    });
  });

  it('renders the empty state message when no transactions are returned', async () => {
    getTransactions.mockResolvedValue({ data: [] });
    render(TransactionList);

    await waitFor(() => {
      expect(screen.getByText('No transactions found. Add one to get started!')).toBeInTheDocument();
    });
  });

  it('renders an error message when the fetch fails', async () => {
    getTransactions.mockRejectedValue(new Error('Failed to fetch'));
    render(TransactionList);

    await waitFor(() => {
      expect(screen.getByText('Error: Failed to fetch')).toBeInTheDocument();
    });
  });

});

import { describe, it, expect, vi } from 'vitest';
import { render, screen } from '@testing-library/svelte';
import userEvent from '@testing-library/user-event';
import ConfirmationModal from './ConfirmationModal.svelte';

describe('ConfirmationModal', () => {
    it('does not render when isOpen is false', () => {
        render(ConfirmationModal, {
            props: {
                isOpen: false,
                message: 'Are you sure?',
                onConfirm: vi.fn(),
                onCancel: vi.fn()
            }
        });

        expect(screen.queryByText('Confirm Action')).not.toBeInTheDocument();
    });

    it('renders correctly when isOpen is true', () => {
        render(ConfirmationModal, {
            props: {
                isOpen: true,
                message: 'Are you sure?',
                onConfirm: vi.fn(),
                onCancel: vi.fn()
            }
        });

        expect(screen.getByText('Confirm Action')).toBeInTheDocument();
        expect(screen.getByText('Are you sure?')).toBeInTheDocument();
        expect(screen.getByText('Confirm')).toBeInTheDocument();
        expect(screen.getByText('Cancel')).toBeInTheDocument();
    });

    it('calls onConfirm when confirm button is clicked', async () => {
        const user = userEvent.setup();
        const onConfirm = vi.fn();
        render(ConfirmationModal, {
            props: {
                isOpen: true,
                message: 'Are you sure?',
                onConfirm,
                onCancel: vi.fn()
            }
        });

        await user.click(screen.getByText('Confirm'));
        expect(onConfirm).toHaveBeenCalled();
    });

    it('calls onCancel when cancel button is clicked', async () => {
        const user = userEvent.setup();
        const onCancel = vi.fn();
        render(ConfirmationModal, {
            props: {
                isOpen: true,
                message: 'Are you sure?',
                onConfirm: vi.fn(),
                onCancel
            }
        });

        await user.click(screen.getByText('Cancel'));
        expect(onCancel).toHaveBeenCalled();
    });
});

