from typing import List, Optional
from fastapi import APIRouter, HTTPException, Depends
from pydantic import BaseModel, Field
from app.db.session import supabase
from app.api import deps
from app.crud import transactions as crud_transactions
import uuid
from datetime import date

router = APIRouter()

# --- Pydantic Models ---
class TransactionCreate(BaseModel):
    amount: float
    type: str  # "income" or "expense"
    date: date
    description: Optional[str] = None
    category_id: uuid.UUID

class Transaction(BaseModel):
    id: uuid.UUID
    amount: float
    type: str
    date: date
    description: Optional[str] = None
    user_id: uuid.UUID
    category_id: uuid.UUID

class TransactionUpdate(BaseModel):
    amount: Optional[float] = None
    type: Optional[str] = None  # "income" or "expense"
    date: Optional[date] = None
    description: Optional[str] = None
    category_id: Optional[uuid.UUID] = None

# --- API Endpoints ---
@router.get("/", response_model=List[Transaction])
def list_transactions(user: dict = Depends(deps.get_current_user)):
    """
    Retrieve all transactions for the current user.
    """
    try:
        user_id = user.user.id
        response = supabase.table('transactions').select('*').match({
            'user_id': user_id
        }).order('date', desc=True).execute()

        if not response.data:
            return []

        return [Transaction(**t) for t in response.data]
    except Exception as e:
        raise HTTPException(status_code=400, detail=str(e))

@router.put("/{transaction_id}", response_model=Transaction)
def update_transaction(transaction_id: uuid.UUID, transaction: TransactionUpdate, user: dict = Depends(deps.get_current_user)):
    """
    Update a transaction for the current user.
    """
    try:
        user_id = user.user.id
        update_data = transaction.dict(exclude_unset=True)

        if not update_data:
            raise HTTPException(status_code=400, detail="No update data provided.")

        response = supabase.table('transactions').update(update_data).match({
            'id': transaction_id,
            'user_id': user_id
        }).execute()

        if not response.data:
            raise HTTPException(status_code=404, detail="Transaction not found or user does not have permission.")

        updated_transaction = response.data[0]
        return Transaction(**updated_transaction)
    except Exception as e:
        raise HTTPException(status_code=400, detail=str(e))

@router.post("/", response_model=Transaction)
def create_transaction(transaction: TransactionCreate, user: dict = Depends(deps.get_current_user)):
    """
    Create a new transaction for the current user.
    """
    try:
        user_id = user.user.id
        response = supabase.table('transactions').insert({
            "amount": transaction.amount,
            "type": transaction.type,
            "date": str(transaction.date),
            "description": transaction.description,
            "category_id": transaction.category_id,
            "user_id": user_id
        }).execute()

        if not response.data:
            raise HTTPException(status_code=500, detail="Failed to create transaction.")

        created_transaction = response.data[0]
        return Transaction(**created_transaction)
    except Exception as e:
        raise HTTPException(status_code=400, detail=str(e))

@router.delete("/{transaction_id}", response_model=dict)
def delete_transaction(transaction_id: uuid.UUID, user: dict = Depends(deps.get_current_user)):
    """
    Delete a transaction for the current user.
    """
    try:
        user_id = user.user.id
        success = crud_transactions.delete_transaction(transaction_id, user_id)

        if not success:
            raise HTTPException(status_code=404, detail="Transaction not found or user does not have permission.")

        return {"detail": "Transaction deleted successfully"}
    except Exception as e:
        if isinstance(e, HTTPException):
            raise e
        raise HTTPException(status_code=400, detail=str(e))

import pytest
from fastapi.testclient import TestClient
from unittest.mock import MagicMock, PropertyMock
import uuid

USER_ID = uuid.uuid4()

class MockSupabaseUser:
    id = USER_ID

class MockUserResponse:
    user = MockSupabaseUser()

@pytest.fixture
def mock_supabase_auth(mocker):
    return mocker.patch(
        "app.api.deps.supabase.auth.get_user",
        return_value=MockUserResponse()
    )

@pytest.fixture
def mock_crud_supabase(mocker):
    # Mocking the supabase client used in the crud module
    return mocker.patch("app.crud.transactions.supabase.table")

def test_delete_transaction_success(client: TestClient, mock_supabase_auth, mock_crud_supabase):
    mock_execute = MagicMock()
    # Return a list with one item to simulate successful deletion
    type(mock_execute).data = PropertyMock(return_value=[{"id": str(uuid.uuid4())}])

    mock_crud_supabase.return_value.delete.return_value.match.return_value.execute.return_value = mock_execute

    transaction_id = uuid.uuid4()
    response = client.delete(
        f"/api/v1/transactions/{transaction_id}",
        headers={"Authorization": "Bearer fake-token"}
    )

    assert response.status_code == 200
    assert response.json() == {"detail": "Transaction deleted successfully"}

    # Verify the correct calls were made
    mock_crud_supabase.assert_called_with('transactions')
    mock_crud_supabase.return_value.delete.assert_called_once()
    mock_crud_supabase.return_value.delete.return_value.match.assert_called_with({
        'id': str(transaction_id),
        'user_id': str(USER_ID)
    })

def test_delete_transaction_not_found(client: TestClient, mock_supabase_auth, mock_crud_supabase):
    mock_execute = MagicMock()
    # Return empty list to simulate not found or not owned by user
    type(mock_execute).data = PropertyMock(return_value=[])

    mock_crud_supabase.return_value.delete.return_value.match.return_value.execute.return_value = mock_execute

    transaction_id = uuid.uuid4()
    response = client.delete(
        f"/api/v1/transactions/{transaction_id}",
        headers={"Authorization": "Bearer fake-token"}
    )

    assert response.status_code == 404
    assert response.json() == {"detail": "Transaction not found or user does not have permission."}

