import uuid
from app.db.session import supabase

def delete_transaction(transaction_id: uuid.UUID, user_id: uuid.UUID) -> bool:
    """
    Deletes a transaction by its ID for a specific user.
    Returns True if deletion was successful (record found and deleted), False otherwise.
    """
    response = supabase.table('transactions').delete().match({
        'id': str(transaction_id),
        'user_id': str(user_id)
    }).execute()

    if response.data:
        return True
    return False

def get_summary(user_id):
    response = supabase.table('transactions').select('amount', 'type').eq('user_id', str(user_id)).execute()

    total_income = 0
    total_expenses = 0

    for transaction in response.data:
        if transaction['type'] == 'income':
            total_income += transaction['amount']
        elif transaction['type'] == 'expense':
            total_expenses += transaction['amount']

    net_balance = total_income - total_expenses

    return {
        "total_income": total_income,
        "total_expenses": total_expenses,
        "net_balance": net_balance
    }

const BASE_URL = import.meta.env.VITE_API_BASE_URL || 'http://localhost:8000';

const getHeaders = () => {
  const headers = {
    'Content-Type': 'application/json',
  };
  // @ts-ignore
  const token = localStorage.getItem('jwt_token');
  if (token) {
    // @ts-ignore
    headers['Authorization'] = `Bearer ${token}`;
  }
  return headers;
};

const handleResponse = async (response) => {
  if (response.status === 204) {
    return {}; // No content to parse
  }
  if (!response.ok) {
    const error = await response.json();
    throw new Error(error.detail || 'API request failed');
  }
  return response.json();
};

const api = {
  get: async (endpoint) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
      method: 'GET',
      headers: getHeaders(),
    });
    return handleResponse(response);
  },
  post: async (endpoint, data, options = {}) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
      method: 'POST',
      headers: { ...getHeaders(), ...options.headers },
      body: data instanceof URLSearchParams ? data.toString() : JSON.stringify(data),
    });
    return handleResponse(response);
  },
  put: async (endpoint, data) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
      method: 'PUT',
      headers: getHeaders(),
      body: JSON.stringify(data)
    });
    return handleResponse(response);
  },
  delete: async (endpoint) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
      method: 'DELETE',
      headers: getHeaders()
    });
    return handleResponse(response);
  }
};

// --- Category Specific Functions ---
export const getCategories = () => {
  return api.get('/api/v1/categories/');
};

export const createCategory = (name: string) => {
  return api.post('/api/v1/categories/', { name });
};

export const updateCategory = (id: number, name: string) => {
  return api.put(`/api/v1/categories/${id}`, { name });
};

export const deleteCategory = (id: number) => {
  return api.delete(`/api/v1/categories/${id}`);
};

// --- Transaction Specific Functions ---
export const getTransactions = () => {
  return api.get('/api/v1/transactions/');
};

export const createTransaction = (transaction) => {
  return api.post('/api/v1/transactions/', transaction);
};

export const updateTransaction = (id, transaction) => {
  return api.put(`/api/v1/transactions/${id}`, transaction);
};

export const deleteTransaction = (id) => {
  return api.delete(`/api/v1/transactions/${id}`);
};

export const getSummary = () => {
  return api.get('/api/v1/dashboard/summary');
};

// --- Auth Specific Functions ---
export const registerUser = (email: string, password: string) => {
  return api.post('/api/v1/auth/signup', { email, password });
};

export const loginUser = (email: string, password: string) => {
  const formData = new URLSearchParams();
  formData.append('username', email);
  formData.append('password', password);
  return api.post('/api/v1/auth/login', formData, {
    headers: { 'Content-Type': 'application/x-www-form-urlencoded' }
  });
};

export default api;

import { writable } from 'svelte/store';
import { getCategories, getTransactions } from '$lib/services/api';

export const categories = writable([]);
export const transactions = writable([]);

export const loadCategories = async () => {
  try {
    const data = await getCategories();
    categories.set(data);
  } catch (error) {
    console.error('Failed to load categories:', error);
  }
};

export const loadTransactions = async () => {
  try {
    const data = await getTransactions();
    transactions.set(data);
  } catch (error) {
    console.error('Failed to load transactions:', error);
  }
};

import { render, screen, waitFor, within } from '@testing-library/svelte';
import { describe, it, expect, vi } from 'vitest';
import FinancialSummary from './FinancialSummary.svelte';

// Mock the API service
vi.mock('$lib/services/api', () => ({
  getSummary: vi.fn(),
}));

import { getSummary } from '$lib/services/api';

describe('FinancialSummary Component', () => {
  it('renders the summary data on successful fetch', async () => {
    const mockSummary = {
      total_income: 1000,
      total_expenses: 500,
      net_balance: 500,
    };
    // @ts-ignore
    getSummary.mockResolvedValue(mockSummary);

    render(FinancialSummary, {
      total_income: mockSummary.total_income,
      total_expenses: mockSummary.total_expenses,
      net_balance: mockSummary.net_balance,
    });

    await waitFor(() => {
      const incomeCard = screen.getByTestId('stat-card-total-income');
      expect(within(incomeCard).getByText('Total Income')).toBeInTheDocument();
      expect(within(incomeCard).getByText('$1,000.00')).toBeInTheDocument();

      const expensesCard = screen.getByTestId('stat-card-total-expenses');
      expect(within(expensesCard).getByText('Total Expenses')).toBeInTheDocument();
      expect(within(expensesCard).getByText('$500.00')).toBeInTheDocument();

      const netBalanceCard = screen.getByTestId('stat-card-net-balance');
      expect(within(netBalanceCard).getByText('Net Balance')).toBeInTheDocument();
      expect(within(netBalanceCard).getByText('$500.00')).toBeInTheDocument();
    });
  });
});
