import uuid
from app.db.session import supabase

def delete_transaction(transaction_id: uuid.UUID, user_id: uuid.UUID) -> bool:
    """
    Deletes a transaction by its ID for a specific user.
    Returns True if deletion was successful (record found and deleted), False otherwise.
    """
    response = supabase.table('transactions').delete().match({
        'id': str(transaction_id),
        'user_id': str(user_id)
    }).execute()

    if response.data:
        return True
    return False

def get_summary(user_id):
    response = supabase.table('transactions').select('amount', 'type').eq('user_id', str(user_id)).execute()

    total_income = 0
    total_expenses = 0

    for transaction in response.data:
        if transaction['type'] == 'income':
            total_income += transaction['amount']
        elif transaction['type'] == 'expense':
            total_expenses += transaction['amount']

    net_balance = total_income - total_expenses

    return {
        "total_income": total_income,
        "total_expenses": total_expenses,
        "net_balance": net_balance
    }

const BASE_URL = import.meta.env.VITE_API_BASE_URL || 'http://localhost:8000';

const getHeaders = () => {
  const headers = {
    'Content-Type': 'application/json',
  };
  // @ts-ignore
  const token = localStorage.getItem('jwt_token');
  if (token) {
    // @ts-ignore
    headers['Authorization'] = `Bearer ${token}`;
  }
  return headers;
};

const handleResponse = async (response) => {
  if (response.status === 204) {
    return {}; // No content to parse
  }
  if (!response.ok) {
    const error = await response.json();
    throw new Error(error.detail || 'API request failed');
  }
  return response.json();
};

const api = {
  get: async (endpoint) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
      method: 'GET',
      headers: getHeaders(),
    });
    return handleResponse(response);
  },
  post: async (endpoint, data, options = {}) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
      method: 'POST',
      headers: { ...getHeaders(), ...options.headers },
      body: data instanceof URLSearchParams ? data.toString() : JSON.stringify(data),
    });
    return handleResponse(response);
  },
  put: async (endpoint, data) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
      method: 'PUT',
      headers: getHeaders(),
      body: JSON.stringify(data)
    });
    return handleResponse(response);
  },
  delete: async (endpoint) => {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
      method: 'DELETE',
      headers: getHeaders()
    });
    return handleResponse(response);
  }
};

